import Web3 from "web3";
const web3 = new Web3(window.web3.currentProvider);
export default web3;

function checkAllBalances() {
  var i = 0;
  eth.accounts.forEach(function(e) {
    console.log(
      "  eth.accounts[" +
        i +
        "]: " +
        e +
        " \tbalance: " +
        web3.fromWei(eth.getBalance(e), "ether") +
        " ether"
    );
    i++;
  });
}
checkAllBalances();
