import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import web3 from "./web3";
import mybank from "./mybank";

class App extends Component {
  state = {
    value: ""
  };

  async componentDidMount() {
    // const owner = await mybank.methods.owner().call();
    // this.setState({ owner });
  }

  render() {
    return (
      <div>
        <h2>My Bank</h2>
        <hr />
        <table>
          {" "}
          <caption>The Three Most Popular JavaScript Libraries</caption>{" "}
          <thead>
            {" "}
            <tr>
              {" "}
              <th>Library</th> <th>jQuery</th> <th>Bootstrap</th>{" "}
              <th>Modernizr</th>{" "}
            </tr>{" "}
          </thead>{" "}
          <tbody>
            {" "}
            <tr>
              {" "}
              <td>Market Share</td> <td>96.1%</td> <td>17.0%</td> <td>14.3%</td>{" "}
            </tr>{" "}
            <tr>
              {" "}
              <td>Absolute Usage</td> <td>70.4%</td> <td>12.4%</td>{" "}
              <td>10.5%</td>{" "}
            </tr>{" "}
          </tbody>{" "}
          <tfoot>
            {" "}
            <tr>
              {" "}
              <td colspan="4">
                <em>Market Share</em> refers to the percentage of sites using
                any JavaScript library that use the specified library.{" "}
                <em>Absolute Usage</em> is the percent of websites surveyed,
                including those that use no JavaScript libraries, that use the
                specified library. All data comes from{" "}
                <a
                  href="https://w3techs.com/technologies/overview/javascript_library/all"
                  target="_blank"
                >
                  W3Techs
                </a>{" "}
                and was accurate in June of 2016.
              </td>{" "}
            </tr>{" "}
          </tfoot>{" "}
        </table>

        <form>
          <h4>Want to try your luck?</h4>
          <div>
            <label>Amount of ethers to enter</label>
            <input
              value={this.state.value}
              onChange={event => this.setState({ value: event.target.value })}
            />
          </div>
        </form>
      </div>
    );
  }
}

export default App;
