import React, { Component } from "react";
import "./App.css";
import web3 from "./web3";
import myBank from "./myBank";

class App extends Component {
  state = {
    owner: "",
    totalBankBalance: "",
    value: ""
  };

  async componentDidMount() {
    const owner = await myBank.methods.owner().call();
    const totalBankBalance = await web3.eth.getBalance(myBank.options.address);
    this.setState({ owner, totalBankBalance });
  }

  onSubmit = async event => {
    event.preventDefault();

    const accounts = await web3.eth.getAccounts();

    await myBank.methods.deposit().send({
      from: accounts[0],
      value: web3.utils.toWei(this.state.value, "ether")
    });
  };

  render() {
    return (
      <div>
        <h2>My Bank</h2>
        <p>This Bank is managed by {this.state.owner}</p>
        <h2>Bank Balance</h2>
        <p>
          Total Balance in the Bank is{" "}
          {web3.utils.fromWei(this.state.totalBankBalance, "ether")} ether!
        </p>
        <form onSubmit={this.onSubmit}>
          <h2>Deposit</h2>
          <div>
            <label>Enter the ethers to deposit ....</label>
            <input
              value={this.state.value}
              onChange={event => this.setState({ value: event.target.value })}
            />
          </div>
          <button>Enter</button>
        </form>
      </div>
    );
  }
}
export default App;
